mapper.select:
SELECT exercise_no, exercise_type_name, exercise_name, r FROM ( SELECT exercise_no, exercise_type_name, exercise_name, rownum as r FROM ( SELECT exer.exercise_no, extp.exercise_type_name, exer.exercise_name FROM exercise exer, exertype extp WHERE exer.exercise_type_no = extp.exercise_type_no
AND ( UPPER(exer.exercise_name) LIKE '%' || #{word} || '%')
ORDER BY exer.exercise_no DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}
