mapper.select:
SELECT notice_no, notice_title, notice_content, notice_word, notice_rdate, notice_cnt, r FROM ( SELECT notice_no, notice_title, notice_content, notice_word, notice_rdate, notice_cnt, rownum as r FROM ( SELECT notice_no, notice_title, notice_content, notice_word, notice_rdate, notice_cnt FROM notice
WHERE (UPPER(notice_title) LIKE '%' || #{word} || '%' OR UPPER(notice_content) LIKE '%' || #{word} || '%' OR UPPER(notice_word) LIKE '%' || #{word} || '%')
ORDER BY notice_rdate DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}
